import React, {Component} from "react"
import {render} from "react-dom"
import {Toast, Confirm, Prompt, ConfirmAuto, Notification} from "./dialog"

class Page extends Component {
	constructor() {
		super()
		this.state = {
			showDialog: true,
			content: "content"
		}
		this.count = 0
	}

	funClick() {
		console.log("funClick")
		this.setState({
			content: this.state.content + this.count
		},() => {
			this.count ++
			if(this.count > 3) this.setState({
				showDialog: false
				}, () => this.count = 0)
			}
		)
	}

	render() {
		let _funClick = this.funClick.bind(this)

		return (
			<div>
				<span onClick={() => this.setState({showDialog: true})}>reshow</span>
				<Prompt
					content="购物车已经存在其他 车型 零件。您要用新的车架号零件来替换它吗？"
					confirm="好的"
					other="重新输入"
					show={this.state.showDialog}
					fun={() => this.setState({showDialog: false})}
					close={() => this.setState({showDialog: false})} />
			</div>
		)
	}
}

render(<Page />, document.getElementById("root"))
